mapper.select:
SELECT E.RNUM , E.POST_CODE AS "code" , E.POST_TITLE AS "title" , E.ITEM_PRICE AS "itemPrice" , E.AUTH_STATUS AS "authStatus" , E.LIKE_COUNT AS "likes" , E.POST_VIEW AS "views" , E.THUMB_PATH AS "thumbPath" FROM (SELECT ROWNUM RNUM , D.POST_CODE , D.POST_TITLE , D.ITEM_PRICE , D.AUTH_STATUS , D.LIKE_COUNT , D.POST_VIEW , D.THUMB_PATH FROM (SELECT A.POST_CODE , A.POST_TITLE , A.ITEM_PRICE , B.AUTH_STATUS , A.LIKE_COUNT , A.POST_VIEW , C.THUMB_PATH FROM POST A JOIN AUTHENTIC B ON (A.AUTH_CODE = B.AUTH_CODE) JOIN IMG_FILE C ON (A.POST_CODE = C.POST_CODE) WHERE A.BLIND_YN = 'N'
AND A.POST_TITLE LIKE '%' || #{title} || '%'
AND A.POST_CONTENT LIKE '%' || #{content} || '%'
AND A.AUTH_CODE = #{authCode}
AND A.ITEM_PRICE BETWEEN #{itemPriceRangeMin} AND #{itemPriceRangeMax}
AND C.IMG_TYPE = 'REPRESENTATIVE' ORDER BY A.POST_CODE DESC) D
WHERE ROWNUM <= #{endRow}) E
WHERE E.RNUM >= #{startRow}

SELECT E.RNUM , E.POST_CODE AS "code" , E.AUTH_STATUS AS "authStatus" , E.POST_TITLE AS "title" , E.USER_ID AS "sellerId" , E.BLIND_YN AS "blindYn" , E.POST_TIME AS "time" FROM (SELECT ROWNUM RNUM , D.POST_CODE , D.AUTH_STATUS , D.POST_TITLE , D.USER_ID , D.BLIND_YN , D.POST_TIME FROM (SELECT A.POST_CODE , B.AUTH_STATUS , A.POST_TITLE , C.USER_ID , A.BLIND_YN , A.POST_TIME FROM POST A JOIN AUTHENTIC B ON (A.AUTH_CODE = B.AUTH_CODE) JOIN "USER" C ON(A.SELLER_CODE = C.USER_CODE)
C.USER_ID = #{sellerId}
AND A.POST_TITLE LIKE '%' || #{title} || '%'
AND A.POST_CONTENT LIKE '%' || #{content} || '%'
AND A.AUTH_CODE = #{authCode}
AND A.BLIND_YN = #{blindYn}
ORDER BY A.POST_CODE DESC) D
WHERE ROWNUM <= #{endRow}) E
WHERE E.RNUM >= #{startRow}

SELECT
	   COUNT(*)
  FROM POST A

  JOIN "USER" B ON (A.SELLER_CODE = B.USER_CODE)



		A.POST_TITLE LIKE '%' || #{title} || '%'


	AND A.POST_CONTENT LIKE '%' || #{content} || '%'


	AND A.AUTH_CODE = #{authCode}


	AND A.ITEM_PRICE BETWEEN #{itemPriceRangeMin} AND #{itemPriceRangeMax}


	AND A.BLIND_YN = #{blindYn}


	AND B.USER_ID = #{sellerId}

SELECT A.POST_CODE AS "code" , C.USER_ID AS "sellerId"
, E.POST_CAT_NAME AS "catName" , B.AUTH_STATUS AS "authStatus" , A.POST_TITLE AS "title" , A.POST_CONTENT AS "content" , A.LIKE_COUNT AS "likeCount" , A.POST_TIME AS "time" , A.SOLD_YN AS "soldYn" , A.REPORT_COUNT AS "reportCount" , A.BLIND_YN AS "blindYn" , A.ITEM_PRICE AS "itemPrice" , A.POST_VIEW AS "view" FROM POST A JOIN AUTHENTIC B ON (A.AUTH_CODE = B.AUTH_CODE) JOIN "USER" C ON (A.SELLER_CODE = C.USER_CODE)
JOIN POST_CAT E ON (A.POST_CAT_CODE = E.POST_CAT_CODE) WHERE A.POST_CODE = #{code}

mapper.insert:
		INSERT
		  INTO Post A
		(
		  A.POST_CODE
        , A.SELLER_CODE
        , A.POST_CAT_CODE
        , A.POST_TITLE
        , A.POST_CONTENT
        , A.ITEM_PRICE
		)
		VALUES
		(
		  SEQ_POST_CODE.NEXTVAL
		, #{sellerCode}
		, 1
		, #{title}
		, #{content}
		, #{itemPrice}
		)

mapper.update:
UPDATE
	   POST A
   SET A.BLIND_YN = #{blindYn}
 WHERE A.POST_CODE = #{code}

UPDATE
	   POST A


		A.POST_TITLE = #{title}


	  , A.POST_CAT_CODE = #{catCode}


	  , A.POST_CONTENT = #{content}


	  , A.ITEM_PRICE = #{itemPrice}



 WHERE A.POST_CODE = #{code}

UPDATE
	   POST A


		A.AUTH_CODE = #{authCode}


	  , A.BLIND_YN = #{blindYn}


	  , A.POST_CAT_CODE = #{catCode}


 WHERE A.POST_CODE = #{code}

mapper.delete:
DELETE
  FROM POST A
 WHERE A.POST_CODE = #{code}
